import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Stepper, Step, StepLabel, Box } from '@mui/material';

const ORDER_STATUSES = [
  "Placed",         
  "Confirmed",
  "Preparing",
  "Out for Delivery",
  "Delivered"
];

const statusToIndex = (status) => {
  // Map backend status to step index
  const idx = ORDER_STATUSES.findIndex(
    s => s.toLowerCase() === status.toLowerCase()
  );
  return idx === -1 ? 0 : idx;
};

const OrderHistory = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const getOrders = async () => {
      try {
        const res = await axios.get(
          'http://localhost:8020/Order/orderhistory',
          { withCredentials: true }
        );
        const data = Array.isArray(res.data)
          ? res.data
          : res.data.orders || [];
        setOrders(data);
      } catch (err) {
        setOrders([]);
      }
    };
    getOrders();
  }, []);

  const handleStatusChange = async (orderId, newStatus) => {
    await axios.patch(`http://localhost:8020/Order/updateStatus/${orderId}`, { status: newStatus }, { withCredentials: true });
    getOrders(); // refetch orders to update UI
  };
  
  return (
    <div style={{
      marginTop: '20px',
      width: '600px',
      marginLeft: '50px',
      fontSize: '20px'
    }}>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        orders.map((order, key) => (
          <Box key={key} sx={{ mb: 5, p: 2, border: '1px solid #eee', borderRadius: 2 }}>
            <h4>Amount: Rs.{order.amount}</h4>
            <h4>Order Status: {order.status}</h4>

            {/* Status Stepper */}
            <Stepper activeStep={statusToIndex(order.status)} alternativeLabel>
              {ORDER_STATUSES.map((label, idx) => (
                <Step key={label}>
                  <StepLabel>{label}</StepLabel>
                </Step>
              ))}
            </Stepper>

            <table style={{ marginTop: 20 }}>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Quantity</th>
                </tr>
              </thead>
              <tbody>
                {order.products.map((product, pkey) => (
                  <tr key={pkey}>
                    <td>{product.name}</td>
                    <td>{product.quantity}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </Box>
        ))
      )}
    </div>
  );
};

export default OrderHistory;
