import React, { useState, useEffect } from 'react';
import axios from 'axios';
import swal from 'sweetalert';
import { useNavigate } from 'react-router-dom';
import '../../assets/styles/profile.css';
axios.defaults.withCredentials = true;

const Profile = () => {
  const [user, setUser] = useState({});
  const [products, setProducts] = useState([]);
  const [isAvailable, setIsAvailable] = useState(false);
  const navigate = useNavigate();

  // Fetch user and products on mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("http://localhost:8090/User/profile");
        setUser(res.data.user);
        setIsAvailable(res.data.user.isAvailable || false);
        const prodRes = await axios.get(`http://localhost:8070/products/${res.data.user._id}/products`);
        setProducts(prodRes.data);
      } catch (err) {
        swal("Error fetching profile info", "", "error");
      }
    };
    fetchData();
  }, []);

  // Handler for availability toggle
  const handleAvailabilityChange = async (e) => {
    const newAvailability = e.target.checked;
    try {
      await axios.patch(
        "http://localhost:8090/User/setAvailability",
        { isAvailable: newAvailability }
      );
      setIsAvailable(newAvailability);
      swal("Availability updated!", "", "success");
    } catch (err) {
      swal("Error updating availability", err.response?.data?.message || "", "error");
    }
  };

  const handleUpdate = (productId) => {
    navigate(`/updateProduct/${productId}`);
  };

  const handleDelete = (product_id) => {
    swal({
      title: "Are you sure?",
      text: "You want to delete this Product?",
      icon: "warning",
      dangerMode: true,
    }).then((willDelete) => {
      if (willDelete) {
        axios.delete(`http://localhost:8070/products/deleteProduct/${product_id}`)
          .then(() => {
            swal("Product is deleted", { icon: "success", buttons: false, timer: 2000 });
            setProducts(products.filter(product => product._id !== product_id));
          })
          .catch(() => {
            swal("Delete failed", "", "error");
          });
      } else {
        swal({ text: "Your Product is saved!", buttons: false, timer: 2000 });
      }
    });
  };

  const handleDeleteAcc = () => {
    swal({
      title: "Are you sure?",
      text: "Do you want to delete your account?",
      icon: "warning",
      dangerMode: true
    }).then((willDelete) => {
      if (willDelete) {
        axios.delete("http://localhost:8090/User/deleteUser")
          .then(() => {
            swal({
              title: "Your account has been deleted",
              icon: "success",
              buttons: false,
              timer: 2000
            }).then(() => {
              setTimeout(() => {
                window.location.href = "/login";
              }, 1500);
            });
          })
          .catch(() => {
            swal("An error occurred while deleting your account", "", "error");
          });
      } else {
        swal({ title: "Your account is saved", buttons: false, timer: 2000 });
      }
    });
  };

  return (
    <div>
      {user && (
        <div>
          <h1>Name: {user.name}</h1>
          <h1>Mobile: {user.mobile}</h1>
          <h1>E-mail: {user.email}</h1>
          <h1>Address: {user.address}</h1>
          <h1>I am a {user.role}</h1>
        </div>
      )}
      <br />

      {/* Seller Availability Toggle */}
      {user.role === "seller" && (
        <div style={{ marginBottom: 20 }}>
          <label>
            <input
              type="checkbox"
              checked={isAvailable}
              onChange={handleAvailabilityChange}
            />
            {' '}Available for orders
          </label>
        </div>
      )}

      <button className="btn btn-info p-1 me-2 btn-small-width" onClick={() => navigate(`/updateUser/${user._id}`)}>UPDATE ACC</button>
      <button className="btn btn-info p-1 me-2 btn-small-width" onClick={() => navigate(`/updatePWD/${user._id}`)}>UPDATE PASSWORD</button>
      <button className="btn btn-danger btn-small-width float-end" onClick={handleDeleteAcc}>DELETE ACC</button>
      <br /><br />

      {user.role === "seller" && (
        <div>
          <button className="btn btn-primary p-1 me-2" onClick={() => navigate("/addProduct")}>ADD PRODUCT</button>
          <h2><center>MY PRODUCTS</center></h2>
          <table className="table">
            <thead className="thead-dark">
              <tr>
                <th><center>ID</center></th>
                <th><center>Name</center></th>
                <th><center>Brand</center></th>
                <th><center>Price</center></th>
                <th><center>Description</center></th>
                <th><center>Weight</center></th>
                <th><center>Actions</center></th>
              </tr>
            </thead>
            <tbody>
              {products && products.length > 0 ? (
                products.map((myProduct) => (
                  <tr key={myProduct._id}>
                    <td><center>{myProduct._id}</center></td>
                    <td><center>{myProduct.name}</center></td>
                    <td><center>{myProduct.brand}</center></td>
                    <td><center>{myProduct.price}</center></td>
                    <td><center>{myProduct.description}</center></td>
                    <td><center>{myProduct.weight}</center></td>
                    <td>
                      <center>
                        <button className="btn btn-info p-1 me-2" onClick={() => handleUpdate(myProduct._id)}>Update</button>
                        <button className="btn btn-danger p-1 me-2" onClick={() => handleDelete(myProduct._id)}>Delete</button>
                      </center>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={7}><center>There are no products</center></td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Profile;
