import React, { useEffect, useState } from "react";
import axios from "axios";

const API_URL = "http://localhost:8070/products";

const SellerDashboard = () => {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({
    name: "",
    brand: "",
    price: "",
    weight: "",
    upload_date: "",
    description: "",
    image: "",
  });

  // Fetch products for this seller
  useEffect(() => {
    fetchSellerProducts();
  }, []);

  const fetchSellerProducts = async () => {
    try {
      // Replace with your actual sellerId logic
      const token = localStorage.getItem("token");
      // The backend expects the sellerId from JWT, so just call:
      const res = await axios.get(`${API_URL}/getBySellerId`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProducts(res.data);
    } catch (err) {
      alert("Error fetching products");
    }
  };

  // Handle form input
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // Add a new product
  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        `${API_URL}/addProduct`,
        form,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setForm({
        name: "",
        brand: "",
        price: "",
        weight: "",
        upload_date: "",
        description: "",
        image: "",
      });
      fetchSellerProducts();
    } catch (err) {
      alert("Error adding product");
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: "auto" }}>
      <h2>Seller Dashboard</h2>
      <h3>Add New Product</h3>
      <form onSubmit={handleAddProduct}>
        <input
          name="name"
          placeholder="Name"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          name="brand"
          placeholder="Brand"
          value={form.brand}
          onChange={handleChange}
        />
        <input
          name="price"
          type="number"
          placeholder="Price"
          value={form.price}
          onChange={handleChange}
          required
        />
        <input
          name="weight"
          type="number"
          placeholder="Weight"
          value={form.weight}
          onChange={handleChange}
          required
        />
        <input
          name="upload_date"
          type="date"
          placeholder="Upload Date"
          value={form.upload_date}
          onChange={handleChange}
          required
        />
        <input
          name="image"
          placeholder="Image URL"
          value={form.image}
          onChange={handleChange}
          required
        />
        <textarea
          name="description"
          placeholder="Description"
          value={form.description}
          onChange={handleChange}
          required
        />
        <button type="submit">Add Product</button>
      </form>
      <hr />
      <h3>Your Products</h3>
      <ul>
        {products.map((prod) => (
          <li key={prod._id} style={{ marginBottom: "1em" }}>
            <b>{prod.name}</b> - â‚¹{prod.price} - {prod.weight}g
            <br />
            <img src={prod.image} alt={prod.name} width={100} />
            <br />
            {prod.description}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SellerDashboard;
